import React, { Component } from "react";
import { Route, NavLink } from "react-router-dom";
import fetchMoviesApi from "../../services/fetchMoviesApi";
import Cast from "../cast/Cast";
import Reviews from "../reviews/Reviews";
import styles from "../movieDetailPage/movieDetailPage.module.css";
export default class MovieDetailPage extends Component {
  state = {
    movies: "",
  };
  componentDidMount() {
    fetchMoviesApi
      .fetchMovieDetalis(this.props.match.params.movieId)
      .then((movies) => this.setState({ movies }));
  }

  render() {
    const { movies } = this.state;
    console.log(movies.release_date.substring(0, 4));
    return (
      <>
        <img
          src={`https://image.tmdb.org/t/p/w500${movies.poster_path}`}
          className={styles.poster}
          alt={movies.title}
        />
        <p>{movies.original_title}</p>
        {/* <p>{movies.release_date.substr(0, 4)}</p> */}

        <ul>
          <li>
            <NavLink to={`${this.props.match.url}/cast`}>Cast</NavLink>
          </li>
          <li>
            <NavLink to={`${this.props.match.url}/reviews`}>Reviews</NavLink>
          </li>
        </ul>

        <Route path={`${this.props.match.path}/cast`} component={Cast} />
        <Route path={`${this.props.match.path}/reviews`} component={Reviews} />
      </>
    );
  }
}
