import React, { Component } from "react";
import { Route, NavLink } from "react-router-dom";
import PropTypes from "prop-types";
import fetchMoviesApi from "../../services/fetchMoviesApi";
import Cast from "../cast/Cast";
import Reviews from "../reviews/Reviews";
import styles from "../movieDetailPage/movieDetailPage.module.css";
import photo from "../not-found.png";
export default class MovieDetailPage extends Component {
  state = {
    movies: "",
  };
  componentDidMount() {
    fetchMoviesApi
      .fetchMovieDetalis(this.props.match.params.movieId)
      .then((movies) => this.setState({ movies }));
  }

  render() {
    const { movies } = this.state;

    return (
      <>
        {movies && (
          <>
            <NavLink to="/" className={styles.link}>
              Go back
            </NavLink>
            <div className={styles.container}>
              <div>
                <img
                  src={
                    movies.poster_path
                      ? `https://image.tmdb.org/t/p/w500${movies.poster_path}`
                      : photo
                  }
                  className={styles.poster}
                  alt={movies.title}
                />
              </div>
              <div className={styles.info}>
                <p className={styles.film}>
                  {movies.original_title} ({movies.release_date.substr(0, 4)})
                </p>
                <span className={styles.average}>
                  {" "}
                  Vote average: {movies.vote_average}
                </span>
                <p className={styles.overview}>{movies.overview}</p>
                <p>
                  <span className={styles.genreTitle}>Genres:</span>
                  {movies.genres.map((genre) => (
                    <span key={genre.id} className={styles.genre}>
                      {genre.name}
                    </span>
                  ))}
                </p>
                <ul className={styles.linkNavigation}>
                  <li>
                    <NavLink
                      to={`${this.props.match.url}/cast`}
                      className={styles.navLink}
                    >
                      Cast
                    </NavLink>
                  </li>
                  <li>
                    <NavLink
                      to={`${this.props.match.url}/reviews`}
                      className={styles.navLink}
                    >
                      Reviews
                    </NavLink>
                  </li>
                </ul>
              </div>
            </div>

            <Route path={`${this.props.match.path}/cast`} component={Cast} />
            <Route
              path={`${this.props.match.path}/reviews`}
              component={Reviews}
            />
          </>
        )}
      </>
    );
  }
}
