import React, { Component } from "react";
import fetchMoviesApi from "../../services/fetchMoviesApi";
export default class HomePage extends Component {
  state = {
    movies: [],
  };

  componentDidMount() {
    fetchMoviesApi
      .fetchTrendingMovies()
      .then((result) => this.setState({ movies }));
  }

  render() {}
}
