import React, { Component } from "react";
import { Route, NavLink } from "react-router-dom";
import fetchMoviesApi from "../../services/fetchMoviesApi";
// import FooterNavigation from "../navigation/footerNavigation";
import Cast from "../cast/Cast";
import HomePage from "../homepage/HomePage";
export default class MovieDetailPage extends Component {
  state = {
    movies: "",
  };
  componentDidMount() {
    fetchMoviesApi
      .fetchMovieDetalis(this.props.match.params.movieId)
      .then((movies) => this.setState({ movies }));
  }

  render() {
    return (
      <>
        {this.state.movies && <p>{this.state.movies.overview}</p>}
        <>
          <ul>
            <li>
              <NavLink to={`${this.props.match.path}/cast`}>Cast</NavLink>
            </li>
            <li>
              <NavLink to={`${this.props.match.path}/reviews`}>Reviews</NavLink>
            </li>
          </ul>
        </>
        <Route path={`${this.props.match.url}/cast`} component={Cast} />
        <Route path={`${this.props.match.url}/reviews`} component={Cast} />
      </>
    );
  }
}
